mapper.select:
  		select count(*) from user
		where isblock = 'N'
		
			
			
				and useremail like #{useremail}
			
			
				and userid like #{userid}

  		select userid, username, usernumber, usernickname, userpassword, useremail, userphonenumber, isblock, ismanager
		from user
		where isblock = 'N'
		
			
			
				and useremail like #{useremail}
			
			
				and userid like #{userid}
			
		
		order by usernumber desc
		limit #{start}, #{count}

  		select userid, username, usernumber, usernickname, userpassword, useremail, userphonenumber, isblock, ismanager
		from user
		where isblock = 'Y'
		
			
			
				and useremail like #{useremail}
			
			
				and userid like #{userid}
			
		
		order by usernumber desc
		limit #{start}, #{count}
