mapper.select:
select count(*)
from qna
where isdelete = 'N'


		and (qtitle like #{qtitle} or qcontent like #{qcontent})


		and qcontent like #{qcontent}


		and qtitle like #{qtitle}

select qnum , groupid , parent , depth , readcount , userid , qtitle , qcreatedate , qcontent , isdelete
from qna
where qnum = #{qnum}
