mapper.select:
select count(*) from questions
where isdelete = 'N'


		and (qtitle like #{qtitle} or qcontent like #{qcontent})


		and qcontent like #{qcontent}


		and qtitle like #{qtitle}

select qnum, userid, qtitle, qcreatedate, isdelete from questions
where isdelete = 'N'


		and (qtitle like #{qtitle} or qcontent like #{qcontent})


		and qcontent like #{qcontent}


		and qtitle like #{qtitle}


order by qnum desc
limit #{start}, #{count}
